import React, { useState } from "react";
import axios from "axios";

const CompatibilityTab = () => {
  const [person1, setPerson1] = useState({ name: "", dob: "", pob: "", tob: "" });
  const [person2, setPerson2] = useState({ name: "", dob: "", pob: "", tob: "" });
  const [compatibilityType, setCompatibilityType] = useState("amoureuse");

  const handleChangePerson1 = (event) => {
    setPerson1({ ...person1, [event.target.name]: event.target.value });
  };

  const handleChangePerson2 = (event) => {
    setPerson2({ ...person2, [event.target.name]: event.target.value });
  };

  const handleChangeCompatibilityType = (event) => {
    setCompatibilityType(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const prompt = `Analyser la compatibilité ${compatibilityType} entre ${person1.name} (né(e) le ${person1.dob} à ${person1.pob}, à ${person1.tob}) et ${person2.name} (né(e) le ${person2.dob} à ${person2.pob}, à ${person2.tob}).`;

    const response = await axios.post("https://api.openai.com/v1/engines/davinci-codex/completions", {
      prompt,
      max_tokens: 150,
      n: 1,
      stop: null,
      temperature: 0.8,
    });

    const result = response.data.choices[0].text.trim();
    alert(`Résultat : ${result}`);
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Inputs for Person 1 */}
      {/* ... */}

      {/* Inputs for Person 2 */}
      {/* ... */}

      {/* Radio buttons for compatibility type */}
      <label>
        <input
          type="radio"
          value="amoureuse"
          checked={compatibilityType === "amoureuse"}
          onChange={handleChangeCompatibilityType}
        />
        Amoureuse
      </label>
      <label>
        <input
          type="radio"
          value="professionnelle"
          checked={compatibilityType === "professionnelle"}
          onChange={handleChangeCompatibilityType}
        />
        Professionnelle
      </label>
      <label>
        <input
          type="radio"
          value="amicale"
          checked={compatibilityType === "amicale"}
          onChange={handleChangeCompatibilityType}
        />
        Amicale
      </label>

      <button type="submit">Analyser la compatibilité</button>
    </form>
  );
};

export default CompatibilityTab;
